#include "Callbacks.h"
///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

namespace ps2emu
{

Callbacks::GifCallback Callbacks::m_gifCallback = 0;
void* Callbacks::m_gifUserData = 0;

///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

void Callbacks::setGifCallback(void* userData, GifCallback function)
{
	m_gifCallback = function;
	m_gifUserData = userData;
}

///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

void Callbacks::callGif(const void* data)
{
	if (!m_gifCallback)
		return;

	m_gifCallback(m_gifUserData, data);
}

///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

}



